  id: string;
  name: string;
  ticker: string;
  valueUsd: number;
  weeklyDeltaPct: number;
};

export type PerformancePoint = {
  month: string;
  profitUsd: number;
};

export type AllocationSlice = {
  name: "ETFs" | "Stocks" | "Bonds" | "Crypto";
  value: number;
  color: string;
};

export type HoldingStatus = "active" | "pending";

