  id: string;
  name: string;
  ticker: string;
  date: string;
  volume: number;
  changePct: number;
  priceUsd: number;
  pnlUsd: number;
  status: HoldingStatus;
};

export type SortKey =
  | "name"
  | "date"
  | "volume"
  | "changePct"
  | "priceUsd"
  | "pnlUsd"
  | "status";

export type SortDir = "asc" | "desc";

