import { queryOptions } from "@tanstack/react-query";
import { finhubApi } from "@functions/finhubApi";

export type GetCompanyProfileQueryKey = readonly ["companyProfile", string];

const getCompanyProfile = async (symbol: string) => {
  const { data } = await finhubApi.getCompanyProfile(symbol);
  return data;
};

const GetCompanyProfileQueryOptions = (symbol: string) => {
  return queryOptions({
    queryKey: ["companyProfile", symbol] as GetCompanyProfileQueryKey,
    queryFn: () => getCompanyProfile(symbol),
    // Company profile data rarely changes, cache for 24 hours
    staleTime: 86400000, // 24 hours in milliseconds
    gcTime: 86400000, // Keep in cache for 24 hours
  });
};

export { GetCompanyProfileQueryOptions };

import { queryOptions } from "@tanstack/react-query";
import { finhubApi } from "@functions/finhubApi";

export type SearchSymbolQueryKey = readonly [
  "symbolSearch",
  string,
  string | undefined
];

const searchSymbol = async (query: string, exchange?: string) => {
  const { data } = await finhubApi.searchSymbol(query, exchange);
  return data;
};

const SearchSymbolQueryOptions = (query: string, exchange?: string) => {
  return queryOptions({
    queryKey: ["symbolSearch", query, exchange] as SearchSymbolQueryKey,
    queryFn: () => searchSymbol(query, exchange),
    // Symbol search is user-triggered and results are relatively stable
    // Cache for 5 minutes to reduce API calls during form interaction
    staleTime: 300000, // 5 minutes in milliseconds
    gcTime: 600000, // Keep in cache for 10 minutes
    // Don't fetch automatically - only when user types
    enabled: query.length > 0,
  });
};

export { SearchSymbolQueryOptions };

