import { useMemo, useEffect, useState, useCallback } from "react";
import {
  Button,
  Card,
  CardBody,
  CardHeader,
  Container,
  Heading,
  IconButton,
  Inline,
  Input,
  Modal,
  Sidebar,
  SidebarContent,
  SidebarFooter,
  SidebarGroup,
  SidebarGroupContent,
  SidebarHeader,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarProvider,
  SidebarTrigger,
  Stack,
  Text,
} from "@components";
import {
  Bell,
  ChartLine,
  ChevronDown,
  Folder,
  HelpCircle,
  Home,
  Moon,
  Newspaper,
  Search,
  Sun,
  Users,
  Wallet,
} from "lucide-react";
import { cn } from "@/utils/cn";
import type { SortKey, SortDir, HoldingRow } from "@/types/dashboard";
import { clampNumber } from "@utils/clampNumber";
import { compareStrings } from "@utils/compareStrings";
import { useDashboardData } from "./hooks/useDashboardData";
import {
  AssetSummaryCard,
  DashboardHeader,
  PerformanceChart,
  AllocationChart,
  HoldingsTable,
  HoldingsMobileCard,
  EmptyHoldings,
} from "./components";

type ThemeMode = "light" | "dark";

export function Dashboard() {
  const [theme, setTheme] = useState<ThemeMode>("light");
  const [range, setRange] = useState<"month" | "week" | "day">("month");
  const [holdingsQuery, setHoldingsQuery] = useState("");
  const [sortKey, setSortKey] = useState<SortKey>("name");
  const [sortDir, setSortDir] = useState<SortDir>("asc");
  const [activeNav, setActiveNav] = useState<
    "Overview" | "Portfolio" | "Wallet" | "Market" | "Community" | "News"
  >("Overview");
  const [confirmRemoveId, setConfirmRemoveId] = useState<string | null>(null);
  const [lastUpdatedSeconds, setLastUpdatedSeconds] = useState(12);

  // Get dashboard data
  const { assets, perfDaily, perfWeekly, perfMonthly, allocation, holdings } =
    useDashboardData();

  // Update "last updated" timer
  useEffect(() => {
    const t = window.setInterval(() => {
      setLastUpdatedSeconds((s) => clampNumber(s + 1, 0, 999));
    }, 1000);
    return () => window.clearInterval(t);
  }, []);

  // Select performance data based on range
  const perf = useMemo(() => {
    if (range === "day") return perfDaily;
    if (range === "week") return perfWeekly;
    return perfMonthly;
  }, [range, perfDaily, perfWeekly, perfMonthly]);

  // Filter and sort holdings
  const visibleHoldings: readonly HoldingRow[] = useMemo(() => {
    const q = holdingsQuery.trim().toLowerCase();
    const filtered = q
      ? holdings.filter(
          (h) =>
            h.name.toLowerCase().includes(q) ||
            h.ticker.toLowerCase().includes(q)
        )
      : holdings;

    const dir = sortDir === "asc" ? 1 : -1;

