  // Create map of symbol -> profile data
  const profileMap = new Map<string, FinnhubCompanyProfile>();
  queries.forEach((query, index) => {
    if (query.data) {
      profileMap.set(symbols[index], query.data);
    }
  });

  return {
    profileMap,
    isLoading,
    isError,
    queries,
  };
}

