                        <span data-slot="label">Wallet</span>
                      </SidebarMenuButton>
                    </SidebarMenuItem>
                    <SidebarMenuItem>
                      <SidebarMenuButton
                        isActive={activeNav === "Market"}
                        onClick={() => setActiveNav("Market")}
